//Write a program to print the area and perimeter of a shape triangle having sides of 3 by creating a class named 'Triangle' without any parameter in its constructorimport java.util.*;class Triangle{	Scanner sc=new Scanner(System.in);	int side1,side2,side3;		public Triangle(){		System.out.print("\nenter side 1 : ");		this.side1=sc.nextInt();		System.out.print("enter side 2 : ");		this.side2=sc.nextInt();		System.out.print("enter side 3 : ");		this.side3=sc.nextInt();	}		public void perimeter(){		int p=side1+side2+side3;		System.out.println("\n\nperimeter of triangle : "+p);	}		public void area(){		double s=(side1+side2+side3)/2;		double a=Math.sqrt(s*(s-side1)*(s-side2)*(s-side3));			System.out.println("\narea of triangle : "+a);	}	}public class AreaDefaultConstructor{	public static void main(String[] args) {				Triangle t=new Triangle();			t.perimeter();		t.area();	}}