//Write a program to demonstrate partial implementation of interface and extending interfaces.interface I1{	void display1();}interface I2 extends I1{	void display2();}abstract class a1 implements I2{	public void display1(){		System.out.println("\nthis is interface 1 method");	}		public void display2(){		System.out.println("\nthis is interface 2 method");	}}class demo extends a1{}public class partial_implementation{	public static void main(String[] args) {		demo a=new demo();		a.display1();		a.display2();	}}